import React, {useState, useEffect} from 'react';
import { Trending } from "../gallary"
import { BrowserRouter, Route, Link } from 'react-router-dom';


export const Home = (e) => {
    const [filteredData, handleFilterData] = useState([])


    const getSpaceData = async (url) => {
        let response = await fetch(`https://api.giphy.com/v1/gifs/search?api_key=Lc4axdf3A4Qp9w04vYvVSmmI2uTSly7n&q=${url}&limit=25&offset=0&rating=g&lang=en`);
        let data = await response.json()
        return handleFilterData(data);
    }

    const filterData = (e) => {
        const getValue = e.target.value;
        getSpaceData(getValue).then(data => data);
    }


    const debounce = (fun, delay) => {
        let timer;
        return () => {
          let context = this;
          clearInterval(timer);
          timer = setTimeout(() => {
            fun.apply(context);
          }, delay);
        };
      };

    return <div>
        <div className="searchForm">
            <form>
                <input
                    placeholder="Search for..."
                    onChange={(e) => debounce(filterData(e), 3000)}
                />
            </form>
{filteredData.data && filteredData.data.map(giffy => {
    return <img className="gif-wrapper" src={giffy.images.original.url}/>
})

}

        </div>

        <BrowserRouter>
            <ul>
                <li><Link to='/treading'>Treading</Link></li>
            </ul>
            <Route exact path='/treading' component={Trending}></Route>
        </BrowserRouter>
    </div>
}
